'',
'    -- Plugin attributes',
'    l_api_key plugin_attr := p_plugin.attribute_01;',
'',
'    -- Component attributes',
'    l_action plugin_attr := p_item.attribute_01;',
'    l_address plugin_attr := p_item.attribute_02;',
'    l_city plugin_attr := p_item.attribute_03;',
