'                                    , p_item.element_width',
'                                    , p_item.element_max_length));',
'',
'l_onload_string :=',
'''',
'$("#%NAME%").placesAutocomplete({',
'  pageItems : {',
'    autoComplete : {',
'      %AUTOCOMPLETE_ID%',
